package datastructaures.sll;public class Node<T> {    T value;    Node next;    public Node(T value) {        this.value = value;    }    public Node(T value, Node next) {        this.value = value;        this.next = next;    }    @Override    public String toString() {        return "Node{" +                "value=" + value +                ", next=" + next +                '}';    }}