package datastructaures.trees;import java.util.ArrayDeque;import java.util.ArrayList;import java.util.List;import java.util.Queue;public class BreadthFirstSearch {    public List<Node> bfs(BinarySearchTree tree) {        List<Node> data = new ArrayList<>();        Queue<Node> queue = new ArrayDeque<>();        if (tree.root == null) {            System.out.println("Tree is empty");            return null;        }        Node node = tree.root;        queue.add(node);        while (queue.size() > 0) {            node = queue.remove();            data.add(node);            if (node.left != null) queue.add(node.left);            if (node.right != null) queue.add(node.right);        }        return data;    }}