package datastructaures.trees;import java.util.ArrayList;import java.util.List;public class DFSPostOrderTraversal {    public List<Node> dfsPostOrder(Node root) {        List<Node> data = new ArrayList<>();        traverse(root, data);        return data;    }    private void traverse(Node node, List<Node> data) {        if (node.left != null) traverse(node.left, data);        if (node.right != null) traverse(node.right, data);        data.add(node);    }}