package datastructaures.trees;import java.util.ArrayList;import java.util.List;public class DFSPreOrderTraversal {    public List<Node> dfsPreOrder(Node root) {        List<Node> data = new ArrayList<>();        traverse(root, data);        return data;    }    private void traverse(Node node, List<Node> data) {        data.add(node);        if (node.left != null) traverse(node.left, data);        if (node.right != null) traverse(node.right, data);    }}