package designpatterns.builder;public class Employee {    private int id;    private String name;    private int salary;    public Employee(Builder builder) {        this.id = builder.id;        this.name = builder.name;        this.salary = builder.salary;    }    public static class Builder {        private int id;        private String name;        private int salary;        public Builder setId(int id) {            this.id = id;            return this;        }        public Builder setName(String name) {            this.name = name;            return this;        }        public Builder setSalary(int salary) {            this.salary = salary;            return this;        }        public Employee build() {            return new Employee(this);        }    }    public int getId() {        return id;    }    public String getName() {        return name;    }    public int getSalary() {        return salary;    }    @Override    public String toString() {        return "Employee{" +                "id=" + id +                ", name='" + name + '\'' +                ", salary=" + salary +                '}';    }}