package designpatterns.singleton;public class Singleton {    private static Singleton singleton;    private int id;    public static Singleton getInstance() {        if (singleton == null) {            singleton = new Singleton(10);        }        return singleton;    }    public Singleton(int id) {        this.id = id;    }    @Override    public String toString() {        return "Singleton{" +                "id=" + id +                '}';    }}