package designpatterns.singleton;public class Singleton {    private static volatile Singleton singleton;    private int id;    public static Singleton getInstance() {        if (singleton == null) {            synchronized (Singleton.class) {                if (singleton == null) {                    singleton = new Singleton(10);                }            }        }        return singleton;    }    private Singleton(int id) {        this.id = id;    }    @Override    public String toString() {        return "Singleton{" +                "id=" + id +                '}';    }}