package designpatterns.solid.o.latest;import designpatterns.solid.o.Product;public class AndSpecification  implements Specification<Product>{    Specification<Product> first, second;    public AndSpecification(Specification<Product> first, Specification<Product> second) {        this.first = first;        this.second = second;    }    @Override    public boolean isSatisfied(Product t) {        return first.isSatisfied(t) && second.isSatisfied(t);    }}