package designpatterns.solid.s;import java.io.File;import java.io.FileNotFoundException;import java.io.IOException;import java.io.PrintStream;import java.util.ArrayList;import java.util.List;// SRP - Single Responsibility Principle// A Class has to perform only one particular functionality, should not do multiple actions// In this example we have 2 class -// 1- Journal -- Which just takes notes// 2- Persistance -- which does operations like - saving to a file/ reading from a file or an URL etc// Combining these 2 class into one class would have broken SRPpublic class SPrinciple {    public static void main(String[] args) throws IOException {        String filename = "C:\\Users\\shash\\Desktop\\angular\\test.txt";        Journal j = new Journal();        j.addEntry("Hi Gud Mrng");        j.addEntry("Evening Time");        j.addEntry("Pubg Time bruh");        System.out.println(j);        Persistance p = new Persistance();        p.saveToFile(j.entries, filename, true);        p.loadFromFile(filename);    }}