package designpatterns.stratergy;public class Demo {    public static void main(String[] args) {        System.out.println("Before Planting Bomb");        Player terrorist = new Player("Terrorist");        Player counterTerrorist = new Player("CT");        terrorist.setStratergy(new Defensive());        counterTerrorist.setStratergy(new Aggressive());        terrorist.action();        counterTerrorist.action();        System.out.println("After Planting Bomb");        counterTerrorist.setStratergy(new Defensive());        terrorist.setStratergy(new Aggressive());        counterTerrorist.action();        terrorist.action();    }}