package multithread.executors;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;public class RunnableExecutorExample {    public static void main(String[] args) {        ExecutorService executorService = Executors.newFixedThreadPool(20);        for (int i = 0; i < 10; i++) {            executorService.execute(new RunnableTask());        }    }    static class RunnableTask implements Runnable {        @Override        public void run() {            System.out.println("inside RunnableExample  impl " + Thread.currentThread().getName());            for (int i = 1; i < 100; i++) {                System.out.println(Thread.currentThread().getName() + " - " + i);            }        }    }}