package multithread.executortypes;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;public class SingleThreadPool {    public static void main(String[] args) {        ExecutorService executorService = Executors.newSingleThreadExecutor();        for (int i = 0; i < 4; i++) {            executorService.execute(new RunnableTask());        }        executorService.shutdown();    }    static class RunnableTask implements Runnable {        @Override        public void run() {            System.out.println("inside RunnableExample  impl " + Thread.currentThread().getName());            for (int i = 1; i < 10; i++) {                System.out.println(Thread.currentThread().getName() + " - " + i);            }        }    }}