package multithread.threads;public class ThreadExample {    public static void main(String[] args) {        ClassicThreadExtension t1 = new ClassicThreadExtension();        ClassicThreadExtension t2 = new ClassicThreadExtension();        t1.start();        t2.start();    }    static class ClassicThreadExtension extends Thread {        @Override        public void run() {            System.out.println("inside classic thread impl " + Thread.currentThread().getName());            for (int i = 1; i < 100; i++) {                System.out.println(Thread.currentThread().getName() + " - " + i);            }        }    }}