package problem.solving.trees;public class LeftViewOfBT {    public static void main(String[] args) {        Node root = new Node(1);        root.left = new Node(2);        root.right = new Node(3);        root.left.right = new Node(4);        root.right.left = new Node(5);        root.right.left.right = new Node(6);        leftView(root,1);    }    static int max_level = 0;    public static void leftView(Node root, int level) {        if (root == null) return;        if (max_level < level) {            System.out.println(root);            max_level++;        }        leftView(root.left, level + 1);        leftView(root.right, level + 1);    }}