package problem.solving.trees;public class RightViewBT {    public static void main(String[] args) {        Node root = new Node(1);        root.left = new Node(2);        root.right = new Node(3);        root.left.right = new Node(4);        root.right.left = new Node(5);        root.right.left.right = new Node(6);        rightView(root, 1);    }    static int max_level_visited = 0;    private static void rightView(Node root, int level) {            if(root == null) return;            if(max_level_visited < level){                System.out.println(root);                max_level_visited ++ ;            }        rightView(root.right, level +1);        rightView(root.left, level +1);    }}